import React, { Component } from 'react';
import { Text, View, TextInput, Button, FlatList, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class ChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      error: null,
      chatSearchId: '',
      singleChat: null,
      ChatName:'',
    };
  }

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.viewChats();
    });
  }

  componentWillUnmount() {
    if (this.focusListener != null && this.focusListener.remove) {
      this.focusListener.remove();
    }
  }

  async viewChats() {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
      });
      if (response.status === 200) {
        const chats = await response.json();
        this.setState({ chats: chats });
      } else if (response.status === 401) {
        console.log('Unauthorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }

  render() {
    const { chats, singleChat } = this.state;

    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.headerText}>Chats</Text>
          <TouchableOpacity onPress={() => this.props.navigation.navigate('NewChat')}>
            <Text style={styles.createChatLinkText}>Create New Chat</Text>
          </TouchableOpacity>
        </View>

        {singleChat && (
          <View style={styles.singleChatContainer}>
            <Text style={styles.chatName}>Chat Name: {singleChat.name}</Text>
            <Text>Last Message: {singleChat.last_message ? singleChat.last_message.message : 'No Chats Have Been Created'}</Text>
          </View>
        )}

        <FlatList
          data={chats}
          renderItem={({ item }) => (
            <TouchableOpacity 
              style={styles.chatContainer}
              onPress={() => this.props.navigation.navigate('SingleChat', { chat_id: item.chat_id })}>
              <Text style={styles.chatName}>{item.name}</Text>
              <Text>{item.last_message.message}</Text>
            </TouchableOpacity>
          )}
          keyExtractor={(item) => item.chat_id.toString()}
        />
      </View>
    );
  }
}

const styles = {
  container: {
    flex: 1,
    paddingHorizontal: 10,
    backgroundColor: '#fff',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginVertical: 10,
  },
  headerText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  createChatLinkText: {
    color: 'blue',
    fontWeight: 'bold',
    textDecorationLine: 'underline',
  },
  chatContainer: {
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  singleChatContainer: {
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  chatName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
};

export default ChatScreen;
