import React, { Component } from 'react';
import { TextInput, Button, Alert, View, Text, FlatList } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';


class NewChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newChatUserId: '',
      newChatName: '',
      contacts: [],
      searchQuery: '',
      filteredContacts: [],
    };
  }

  ChatName = (text) => {
    this.setState({ newChatName: text });
  }  

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.fetchContacts();
    });
  }

  fetchContacts = async () => {
    try {
      const response = await this.getContacts();
      this.setState({ contacts: response });
    } catch (error) {
      console.error(error);
    }
  };

  SpecificContactSearch = (searchQuery) => {
    this.setState({ searchQuery });
  };

  SearchContacts = () => {
    this.filterContacts();
  };

  AddToChat = (user_id) => {
    const { newChatUserId } = this.state;
  
    if (newChatUserId) {
      this.AddUserToChat(newChatUserId, user_id);
    }
  };  

  getContacts = async () => {
    const response = await fetch('http://localhost:3333/api/1.0.0/contacts', {
      method: 'GET',
      headers: {
        'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    });
    if (response.status !== 200) {
      throw new Error('Failed to fetch contacts');
    }
    const contacts = await response.json();
    return contacts;
  };
  
  filterContacts = () => {
    const { searchQuery, contacts } = this.state;
    const filteredContacts = contacts.filter(contact =>
      String(contact.user_id).toLowerCase().includes(searchQuery.toLowerCase()) ||
      String(contact.first_name).toLowerCase().includes(searchQuery.toLowerCase()) ||
      String(contact.last_name).toLowerCase().includes(searchQuery.toLowerCase())
    );
    this.setState({ filteredContacts });
  };

  async startConversation() {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      
      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'POST',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({name: this.state.newChatName}),
      });
      console.log("Response: ", response);
     if (response.status === 201) {
      const chatDetails = await response.json();
      console.log("Chat Details: ", chatDetails);
      this.setState({ newChatUserId: chatDetails.id });
      this.props.navigation.navigate('AddToChatScreen', { chatId: chatDetails.id });
      return chatDetails;  
      }
      else if (response.status === 400) {
        throw 'Bad Request';
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.log("Error in startConversation: ", error);
      Alert.alert('Error', error.toString());
    }
  }

  createNewChat = async () => {
    try {
      console.log("Attempting to start chat...");
      const chatDetails = await this.startConversation();
      Alert.alert('Success', 'Chat created');
    } catch (error) {
      console.log("Failed to start chat: ", error);
      Alert.alert('Error', error.toString());
    }
  };
  
  render() {
    const { newChatName, filteredContacts, searchQuery } = this.state;

    return (
      <View>

         
         <TextInput
        placeholder="Enter Chat Name"
        value={newChatName}
        onChangeText={this.ChatName}
      />
        <Button title="Create Chat" onPress={this.createNewChat} 
         
        />
      </View>
    );
  }
}

export default NewChatScreen;


