import React, { Component } from 'react';
import { Text, View, TextInput, Button, Alert } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class NewChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chatName: '',
      newChatUserId: '',
      newMessage: '',
    };
  }

  handleChatNameChange = (text) => {
    this.setState({ chatName: text });
  }

  handleChatIdInputChange = (text) => {
    this.setState({ newChatUserId: text });
  }

  handleNewMessageChange = (text) => {
    this.setState({ newMessage: text });
  }

  createNewChat = async () => {
    const { chatName, newChatUserId, newMessage } = this.state;

    if (chatName && newChatUserId && newMessage) {
      try {
        console.log("Attempting to start conversation...");
        const chatDetails = await this.startConversation(chatName, newChatUserId);
        Alert.alert('Success', 'New chat created successfully');
        // Send a message to the newly created chat
        await this.SendMessage(chatDetails.chat_id, newMessage);
      } catch (error) {
        console.log("Failed to start conversation: ", error);
        Alert.alert('Error', error.toString());
      }
    } else {
      Alert.alert('Error', 'Please input chat name, User ID');
    }
  };

  async startConversation(chatName, userId) {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      
      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'POST',
        headers: {
          'X-Authorization':await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: chatName, userId: userId }),
        
      });
  
      console.log("Response: ", response);
  
      if (response.status === 201) {
        const chatDetails = await response.json();
        console.log("Chat Details: ", chatDetails);
        return chatDetails;
      } else if (response.status === 400) {
        throw 'Bad Request';
        
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.log("Error in startConversation: ", error);
      Alert.alert('Error', error.toString());
    }
  }

  render() {
    const { chatName, newChatUserId, newMessage } = this.state;

    return (
      <View>
        <TextInput
          placeholder="Chat name"
          value={chatName}
          onChangeText={this.handleChatNameChange}
        />

        <TextInput
          placeholder="Enter user ID or email or full name"
          value={newChatUserId}
          onChangeText={this.handleChatIdInputChange}
        />

        <Button title="Create Chat" onPress={this.createNewChat} />
      </View>
    );
  }
}

export default NewChatScreen;
