import React, { Component } from 'react';
import { Text, View, TextInput, Button, FlatList, AsyncStorage, TouchableOpacity } from 'react-native';

class ChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      chatSearchId: '',
      singleChat: null,
    };
  }

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.viewChats();
    });
  }

  componentWillUnmount() {
    if (this.focusListener != null && this.focusListener.remove) {
      this.focusListener.remove();
    }
  }

  async viewChats() {
    try {
      const sessionToken = await AsyncStorage.getItem('whatsthat_session_token');
      if (sessionToken) {
        const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
          method: 'GET',
          headers: {
            'X-Authorization': sessionToken,
            'Content-Type': 'application/json',
          },
        });
        if (response.status === 200) {
          const chats = await response.json();
          this.setState({ chats });
          console.log(this.state.chats);
        } else if (response.status === 401) {
          console.log('Unauthorised');
          await AsyncStorage.removeItem('whatsthat_session_token');
          await AsyncStorage.removeItem('whatsthat_user_id');
          this.props.navigation.navigate('Login');
        } else {
          throw 'Server Error';
        }
      } else {
        // Handle the case when the session token is not set
        console.log('Session token not set');
        this.props.navigation.navigate('Login');
      }
    } catch (error) {
      console.error(error);
    }
  }

  async viewSingleChat(chat_id) {
    try {
      const sessionToken = await AsyncStorage.getItem('whatsthat_session_token');
      if (sessionToken) {
        const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}`, {
          method: 'GET',
          headers: {
            'X-Authorization': sessionToken,
            'Content-Type': 'application/json',
          },
        });

        if (response.status === 200) {
          const chat = await response.json();
          this.setState({ singleChat: chat });
        } else if (response.status === 401) {
          console.log('Unauthorised');
          await AsyncStorage.removeItem('whatsthat_session_token');
          await AsyncStorage.removeItem('whatsthat_user_id');
          this.props.navigation.navigate('Login');
        } else if (response.status === 403) {
          console.log('Forbidden');
        } else if (response.status === 404) {
          console.log('Not Found');
        } else {
          throw 'Server Error';
        }
      } else {
        // Handle the case when the session token is not set
        console.log('Session token not set');
        this.props.navigation.navigate('Login');
      }
    } catch (error) {
      console.error(error);
    }
  }

  renderChatItem = ({ item }) => {
    return (
      <View>
        <Text>Chat ID: {item.chat_id}</Text>
        <Text>Chat Name: {item.name}</Text>
        <Text>Creator: {item.creator.first_name} {item.creator.last_name}</Text>
        <Text>Last Message: {item.last_message ? item.last_message.message : 'No messages yet'}</Text>
      </View>
    );
  };

  render() {
    const { chats, singleChat, chatSearchId } = this.state;
    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.headerText}>Chats</Text>
          <TouchableOpacity onPress={() => this.props.navigation.navigate('NewChat')}>
            <Text style={styles.createChatLinkText}>Create New Chat</Text>
          </TouchableOpacity>
        </View>

        <TextInput
          style={styles.input}
          placeholder="Enter chat ID"
          value={chatSearchId}
          onChangeText={(text) => this.setState({ chatSearchId: text })}
        />
        <Button title="Search" onPress={() => this.viewSingleChat(chatSearchId)} />

        <FlatList
          data={chats}
          keyExtractor={(item) => item.chat_id.toString()}
          renderItem={this.renderChatItem}
        />

        {singleChat && (
          <View>
            <Text>Chat ID: {singleChat.chat_id}</Text>
            <Text>Chat Name: {singleChat.name}</Text>
            <Text>Creator: {singleChat.creator.first_name} {singleChat.creator.last_name}</Text>
            <Text>Last Message: {singleChat.last_message ? singleChat.last_message.message : 'No messages yet'}</Text>
          </View>
        )}
      </View>
    );
  }
}

const styles = {
  container: {
    flex: 1,
    paddingHorizontal: 10,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginVertical: 10,
  },
  headerText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  createChatButton: {
    alignSelf: 'center',
    marginVertical: 10,
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: '#007AFF',
  },
  createChatButtonText: {
    color: '#fff',
  },
  createChatLinkText: {
    color: 'blue',
    textDecorationLine: 'underline',
  },
  input: {
    padding: 10,
    borderWidth: 2,
    borderRadius: 5,
    marginVertical: 10,
  },
};

export default ChatScreen;
