import React, { Component } from 'react';
import { Text, View, TextInput, Button, Modal, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class AddContactsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      user_id: '',
      modalVisible: false,
      errorMessage: '',
    };
  }

  setModalVisible = (visible, message) => {
    this.setState({ modalVisible: visible, errorMessage: message });
  };

  AddUser = async (user_id) => {
    try {
      const currentUserId = await AsyncStorage.getItem('whatsthat_user_id');

      if (currentUserId === user_id) {
        this.setModalVisible(true, "You can't add yourself as a contact");
        return;
      }

      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/contact`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          user_id: this.state.user_id,
        }),
      });

      if (response.status === 200) {
        this.props.route.params.onRefreshContacts();
        this.setModalVisible(true, 'Contact added successfully');
      } else if (response.status === 201) {
        this.setModalVisible(true, "This user is already a contact.");
      } else if (response.status === 401) {
        this.setModalVisible(true, "Unauthorised");
      } else if (response.status === 404) {
        this.setModalVisible(true, "Not Found");
      } else {
        this.setModalVisible(true, "Server Error");
      }
    } catch (error) {
      console.error(error);
    }
  };

  render() {
    const { modalVisible, errorMessage } = this.state;

    return (
      <View>
        <Text>This is the Add Contacts screen</Text>
        <TextInput
          placeholder="Enter The User ID Of The Person You Want To Add"
          onChangeText={(user_id) => this.setState({ user_id })}
          value={this.state.user_id}
          style={{
            padding: 10,
            borderWidth: 2,
            borderRadius: 5,
            margin: 10,
          }}
        />

        <Button title="Add Contact" onPress={() => this.AddUser(this.state.user_id)} />

        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => {
            this.setModalVisible(false);
          }}
        >
          <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <View style={{ backgroundColor: 'white', padding: 20, borderRadius: 10 }}>
              <Text>{errorMessage}</Text>
              <TouchableOpacity
                style={{ backgroundColor: 'blue', padding: 10, borderRadius: 5, marginTop: 10 }}
                onPress={() => {
                  this.setModalVisible(false);
                  if (errorMessage === 'Contact added successfully') {
                    this.props.navigation.goBack();
                  }
                }}
              >
                <Text style={{ color: 'white' }}>Close</Text>
              </TouchableOpacity>
            </View>
          </View>
        </Modal>
      </View>
    );
  }
}

export default AddContactsScreen;
