import React, { Component } from 'react';
import { Text, View } from 'react-native';

class BlockedScreen extends Component {

  async GetBlockedList(){


    return fetch("http://localhost:3333/api/1.0.0/",{
        method: "GET",
        headers: {
        'content-type': 'application/json',
        "X-Authorisation": await AsyncStorage.getItem("whatsthat_user_id"),
      
    }
})


.then((response) => {
if(response.status === 200){
  return response.json();
}else if(response.status === 401){
    throw "Unauthorised"
}else {
  throw "Server Error"
}
})
}


async BlockUser(){

    return fetch("http://localhost:3333/api/1.0.0/",{
        method: "POST",
        headers: {
        "X-Authorisation": await AsyncStorage.getItem("whatsthat_user_id"),
        body: JSON.stringify({
            "user_id": this.state.user_id
            
        })
      
    }
})


.then((response) => {
if(response.status === 200){
  return response.json();
}else if(response.status === 400){
    throw "You can't block yourself"
}else if(response.status === 401){
    throw "Unauthorised"
}else if(response.status === 404){
    throw "Not Found"
}else {
  throw "Server Error"
}
})
}

async UnblockUser(){

    return fetch("http://localhost:3333/api/1.0.0/",{
        method: "DELETE",
        headers: {
        "X-Authorisation": await AsyncStorage.getItem("whatsthat_user_id"),
        body: JSON.stringify({
        "user_id": this.state.user_id
        })
      
    }
})


.then((response) => {
if(response.status === 200){
  return response.json();
}else if(response.status === 400){
  throw "You Can't Block Yourself"
}else if(response.status === 401){
    throw "Unauthorised"
}else if(response.status === 404){
    throw "Not Found"
}else {
  throw "Server Error"
}
})
}


  render() {
    return (
      <View>
        <Text>This is the Blocked screen</Text>
      </View>
    );
  }
}

export default BlockedScreen;
