import React, { Component } from 'react';
import { Text, View, TextInput, Button, FlatList, AsyncStorage } from 'react-native';

class NewChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      chatDetails: null,
      messages: [],
      error: null,
      submitted: false,
      newChatUserId: '',
      newMessage: '',
      currentChatId: null,
      currentMessageId: null,
      editingMessage: false,
    };
  }


  createNewChat = async () => {
    if (this.state.newChatUserId) {
      await this.startConversation([this.state.newChatUserId]);
    }
  }

  sendMessage = async () => {
    if (this.state.newMessage && this.state.currentChatId) {
      await this.SendMessage(this.state.currentChatId, this.state.newMessage);
      this.setState({ newMessage: '' });
    }
  }

  updateMessage = async () => {
    if (this.state.newMessage && this.state.currentChatId && this.state.currentMessageId) {
      await this.UpdateMessage(this.state.currentChatId, this.state.currentMessageId, this.state.newMessage);
      this.setState({ newMessage: '', editingMessage: false, currentMessageId: null });
    }
  }

  handleChatIdInputChange = (text) => {
    this.setState({ newChatUserId: text });
  }

  handleMessageInputChange = (text) => {
    this.setState({ newMessage: text });
  }


async startConversation(userIds) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats', {
        method: 'POST',
        headers: {
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ users: userIds }),
      });

      if (response.status === 201) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
        this.props.navigation.navigate('ChatDetails');
      } else if (response.status === 400) {
        throw 'Bad Request';
    } else if (response.status === 401) {
        throw 'Unathorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }
  async SendMessage(chatId, message) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/messages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          message: message,
        }),
      });
  
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }
  
  async UpdateMessage(chatId, messageId, message) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/messages/${messageId}', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          message: message,
        }),
      });
  
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  } 

  render() {
    const { newChatUserId, newMessage, editingMessage } = this.state;

    return (
      <View>
        <Text>Create new chat</Text>
        <TextInput
          placeholder="Enter user ID"
          value={newChatUserId}
          onChangeText={this.handleChatIdInputChange}
        />
        <Button title="Create Chat" onPress={this.createNewChat} />
        <Text>Messages</Text>
        {/* Render the list of messages here */}
        <TextInput
          placeholder="Type your message"
          value={newMessage}
          onChangeText={this.handleMessageInputChange}
        />
        {!editingMessage ? (
          <Button title="Send Message" onPress={this.sendMessage} />
        ) : (
          <Button title="Update Message" onPress={this.updateMessage} />
        )}
      </View>
    );
  }
}

export default NewChatScreen;
