import React, { Component } from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { Camera } from 'expo-camera';

class CameraScreen1 extends Component{
  constructor(props){
    super(props);

    this.state = {
      // hasPermission: null,
      // type: Camera.Constants.Type.back
      location: null,
      error_message: null
    }
  }

//   async componentDidMount(){
//     const { status } = await Camera.requestPermissionsAsync();
//     this.setState({hasPermission: status === 'granted'});
// }

   componentDidMount(){
    Location.requestForegroundPermissionsAsync()
    .then((result) => {
      let status = result.status;

      if (status !== 'granted'){
        this.setState({error_message: " Permission to location Denied"});
        throw 'Permissions Error';
      }

      Location.getCurrentPositionAsync({})
      .then((pos) => {
        this.setState({location: pos});
      })
    })

    .catch((err) => {
      console.log(err);
    })
  }

  sendToServer = async (data) => {
    let res = await fetch(data.base64);
    let blob = await res.blob();
    
  
    return fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/photo`, {
      method: "POST",
      headers: {
        "Content-Type": "image/png",
        "X-Authorization": token,
      },
      body: blob,
    })
      .then((response) => {
        console.log("Picture added", response);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  
  takePicture = async () => {
    if (this.camera) {
      const options = {
        quality: 0.5,
        base64: true,
        onPictureSaved: (data) => this.sendToServer(data, user_id, token), // Pass user_id and token here
      };
      await this.camera.takePictureAsync(options);
    }
  };
  

  render(){
    if(this.state.hasPermission){
      return(
        <View style={styles.container}>
          <Camera 
            style={styles.camera} 
            type={this.state.type}
            ref={ref => this.camera = ref}
          >
            <View style={styles.buttonContainer}>
              <TouchableOpacity
                style={styles.button}
                onPress={() => {
                  this.takePicture();
                }}>
                <Text style={styles.text}> Take Photo </Text>
              </TouchableOpacity>
            </View>
          </Camera>
        </View>
      );
    }else{
      return(
        <Text>No access to camera</Text>
      );
    }
  }
}

export default CameraScreen1;

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  camera: {
    flex: 1,
  },
  buttonContainer: {
    flex: 1,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    margin: 20,
  },
  button: {
    flex: 0.1,
    alignSelf: 'flex-end',
    alignItems: 'center',
  },
  text: {
    fontSize: 18,
    color: 'white',
  },
});
