import React, { Component } from 'react';
import { Text, View } from 'react-native';

class AddContactsScreen extends Component {
  
async AddUser(){

  return fetch("http://localhost:3333/api/1.0.0/",{
      method: "POST",
      headers: {
      "X-Authorisation": await AsyncStorage.getItem("whatsthat_user_id"),
      body: JSON.stringify({
      "user_id": this.state.user_id
      })
    
  }
})


.then((response) => {
if(response.status === 200){
return response.json();
}else if(response.status === 400){
throw "You Can't add yourself as a contact"
}else if(response.status === 404){
  throw "Unauthorised"
}else {
throw "Server Error"
}
})
}



async DeleteUser(){

  return fetch("http://localhost:3333/api/1.0.0/",{
      method: "DELETE",
      headers: {
      "X-Authorisation": await AsyncStorage.getItem("whatsthat_user_id"),
      body: JSON.stringify({
      "user_id": this.state.user_id
      })
    
  }
})


.then((response) => {
if(response.status === 200){
return response.json();
}else if(response.status === 400){
throw "You Can't Remove Yourself As A Contact"
}else if(response.status === 404){
  throw "Unauthorised"
}else {
throw "Server Error"
}
})
}


  render() {
    return (
      <View>
        <Text>This is the Add Contacts screen</Text>
      </View>
    );
  }
}

export default AddContactsScreen;
