import React, { Component } from 'react';
import { Text, View, Button, TextInput, Alert, map, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class BlockedScreen extends Component {
  state = {
    user_id: '',
    first_name: '',
    last_name: '',
    email: '',
    blockedUsers: [],
  };

  async getBlockedList() {
    try {
      const response = await fetch("http://localhost:3333/api/1.0.0/blocked", {
        method: "GET",
        headers: {
          'Content-Type': 'application/json',
          "X-Authorization": await AsyncStorage.getItem("whatsthat_session_token")
        }
      });
  
      if (response.status === 200) {
        const data = await response.json();
        Alert.alert("Blocked List", JSON.stringify(data));
        this.setState({ blockedUsers: data }); // Set state here
      } else if (response.status === 401) {
        throw "Unauthorised";
      } else {
        throw "Server Error";
      }
    } catch (error) {
      Alert.alert("Error", error.toString());
    }
  }  


  async unblockUser() {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${this.state.user_id}/block`, {
        method: "DELETE",
        headers: {
          'Content-Type': 'application/json',
          "X-Authorization": await AsyncStorage.getItem("whatsthat_session_token"),
        },
        body: JSON.stringify({
          "user_id": this.state.user_id
        })
      });

      if (response.status === 200) {
        Alert.alert("Success", "User unblocked successfully");
        this.getBlockedList();
        this.props.navigation.navigate('ManageContacts');
      } else if (response.status === 400) {
        throw "You can't unblock yourself";
      } else if (response.status === 401) {
        throw "Unauthorised";
      } else if (response.status === 404) {
        throw "Not Found";
      } else {
        throw "Server Error";
      }
    } catch (error) {
      Alert.alert("Error", error.toString());
    }
  }

  render() {
    return (
      <View>
     <TouchableOpacity>
        <TextInput
          value={this.state.user_id}
          onChangeText={user_id => this.setState({ user_id })}
          placeholder={'Enter User ID Of The User You Want To Unblock'}
          style={{
            padding: 10,
            borderWidth: 2,
            borderRadius: 5,
            margin: 10,
          }}
        />
        </TouchableOpacity>

          <Button
            title="Unblock User"
            onPress={() => this.unblockUser()}

          />



<Button
          title="Get Blocked List"
          onPress={() => this.getBlockedList()}
        />
        <Text>Blocked Users:</Text>
        {this.state.blockedUsers.map((user, index) => (
          <View key={index}>
            <Text>ID: {user.user_id}</Text>
            <Text>Name: {user.first_name}</Text>
            <Text>Surname: {user.last_name}</Text>
            <Text>Email: {user.email}</Text>
            
          </View>
        ))}
      </View>
    );
  }
}

export default BlockedScreen;
