import React, { Component } from 'react';
import { Text, View, Image, Button, Alert } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class ProfileScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        firstName: '',
        lastName: '',
        email: '',
        id: '',
        profilePicture: '',
      },
    };
  }

  componentDidMount() {
    this.getCurrentUserInfo();
  }

  getCurrentUserInfo = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
    const session_token = await AsyncStorage.getItem('whatsthat_session_token');
    console.log("Session token:", session_token);

    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/{user_id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': session_token,
        },
      });

      if (response.status === 200) {
        const user = await response.json();
        this.setState({ user });
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else if (response.status === 404) {
        throw 'Not Found';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      Alert.alert('Error', error.toString());
    }
  };

  handleEditProfile = () => {
    this.props.navigation.navigate('UpdateProfileScreen');
  };

  render() {
    const { user } = this.state;

    return (
      <View>
        <Image source={{ uri: user.profilePicture }} style={{ width: 100, height: 100 }} />
        <Text>First Name: {user.firstName}</Text>
        <Text>Last Name: {user.lastName}</Text>
        <Text>Email: {user.email}</Text>
        <Text>ID: {user.id}</Text>
        <Button title="Edit Profile" onPress={this.handleEditProfile} />
      </View>
    );
  }
}

export default ProfileScreen;
