import React, { Component } from 'react';
import { Text, View, TextInput, Button, Alert, Image, StyleSheet } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class UpdateProfileScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        first_name: '',
        last_name: '',
        email: '',
        password: '',
        oldPassword: '',
        profilePicture: null
      },
      isLoading: false
    };
  }

  componentDidMount() {
    this.getCurrentUserInfo();
    this.GPI();
  }

  getCurrentUserInfo = async () => {
  const user_id = await AsyncStorage.getItem('whatsthat_user_id');

  try {
    const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    });

    if (response.status === 200) {
      const user = await response.json();
      this.setState({ user }); 
    } else if (response.status === 401) {
      throw 'Unauthorised';
    } else if (response.status === 404) {
      throw 'Not Found';
    } else {
      throw 'Server Error';
    }
  } catch (error) {
    Alert.alert('Error', error.toString());
  }
};



  handleInputChange = (field, value) => {
    this.setState((prevState) => ({
      user: {
        ...prevState.user,
        [field]: value,
      },
    }));
  };
  updateUserInfo = async () => {
    const { user } = this.state;
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
  
    // Prepare data to send in the PATCH request
    const data = {
      first_name: user.first_name,
      last_name: user.last_name,
      email: user.email,
      old_password: user.oldPassword,
      new_password: user.password
    };
  
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify(data), // Send the prepared data
      });
  
      if (response.status === 200) {
        Alert.alert('Success', 'User info updated successfully');
        this.getCurrentUserInfo(); // Refresh the profile information
      } else if (response.status === 401) {
        throw new Error('Unauthorised');
      } else {
        throw new Error('Failed to update user info');
      }
    } catch (error) {
      Alert.alert('Error', error.toString());
    }
  };
  

  GPI = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
    
    try {
      let response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/photo`, {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        }
      });
  
      let resBlob = await response.blob();
      let data = URL.createObjectURL(resBlob);
  
      this.setState(prevState => ({
        user: {
          ...prevState.user,
          profilePicture: data
        },
        isLoading: false
      }));
    } catch(err) {
      console.log("error", err);
    }
  };
  


  navigateToCamera = () => {
    this.props.navigation.navigate('Camera');
  };

  render() {
    const { user } = this.state;

    return (
      <View style={styles.container}>
        <Image source={{ uri: user.profilePicture }} style={styles.profilePicture} />
        <Button title="Go to Camera" onPress={this.navigateToCamera} />

        <Text style={styles.label}>First Name:</Text>
        <TextInput
          style={styles.input}
          value={user.first_name || ''}
          onChangeText={(text) => this.handleInputChange('first_name', text)}
        />

        <Text style={styles.label}>Last Name:</Text>
        <TextInput
          style={styles.input}
          value={user.last_name || ''}
          onChangeText={(text) => this.handleInputChange('last_name', text)}
        />

        <Text style={styles.label}>Email:</Text>
        <TextInput
          style={styles.input}
          value={user.email || ''}
          onChangeText={(text) => this.handleInputChange('email', text)}
        />

        <Text style={styles.label}>Old Password:</Text>
        <TextInput
          style={styles.input}
          secureTextEntry={true}
          value={user.oldPassword || ''}
          onChangeText={(text) => this.handleInputChange('oldPassword', text)}
        />

        <Text style={styles.label}>New Password:</Text>
        <TextInput
          style={styles.input} 
          secureTextEntry={true}
          value={user.password || ''}
          onChangeText={(text) => this.handleInputChange('password', text)}
        />

        <Button title="Update Info" onPress={this.updateUserInfo} />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  profilePicture: {
    width: 100,
    height: 100,
    marginBottom: 16,
    borderRadius: 50,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 16,
    paddingHorizontal: 8,
  },
});

export default UpdateProfileScreen;
