import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import { StyleSheet, Text, View, Button, FlatList, TextInput, Alert } from 'react-native';

class ContactScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contacts: [],
      searchQuery: '',
      password: '',
    };
  }

  static navigationOptions = {
    header: null,
  };

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.fetchContacts();
    });
  }

  refreshContactsList = () => {
    this.fetchContacts();
  };

  handleSearchChange = (searchQuery) => {
    this.setState({ searchQuery });
  };

  handleSearch = async () => {
    if (this.state.searchQuery) {
      const user = await this.searchUsers(this.state.searchQuery);
      this.setState({ contacts: user ? [user] : [] });
    } else {
      this.fetchContacts();
    }
  };

  searchUsers = async (searchQuery) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${encodeURIComponent(searchQuery)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const user = await response.json();
        return user;
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  fetchContacts = async () => {
    try {
      const response = await this.getContacts();
      this.setState({ contacts: response });
    } catch (error) {
      console.error(error);
    }
  };

  getContacts = async () => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch('http://localhost:3333/api/1.0.0/contacts', {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        return response.json();
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  removeContact = async (user_id) => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch(`http://localhost:3333/api/1.0.0/contacts/${user_id}`, {
        method: 'DELETE',
        headers: {
          'X-Authorization': token,
        },
      });

      if (response.status === 200) {
        Alert.alert('Success', 'Contact removed successfully');
        this.refreshContactsList();
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
      Alert.alert('Error', 'Failed to remove contact');
    }
  };

  blockUser = async (user_id) => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/block`, {
        method: 'POST',
        headers: {
          'X-Authorization': token,
        },
      });
  
      if (response.status === 200) {
        Alert.alert('Success', 'User blocked successfully');
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
      Alert.alert('Error', 'Failed to block user');
    }
  };
  
  // showUserProfile = (user_id) => {
  //   Alert.alert('User Profile', `Name: ${user_id.first_name} ${user_id.last_name}\nEmail: ${user_id.email}`);
  // };

  showUserProfile = (item) => {
    Alert.alert('User Profile', `Name: ${item.first_name} ${item.last_name}\nEmail: ${item.email}`);
  };


  render() {
    return (
      <View style={styles.container}>
        <TextInput
          value={this.state.searchQuery}
          onChangeText={this.handleSearchChange}
          placeholder="Type In The User's ID To Search."
          style={styles.input}
        />

        <Button title="Search" onPress={this.handleSearch} />

        <Button
          title="Manage Contacts"
          onPress={() =>
            this.props.navigation.navigate('ManageContacts', {
              onRefreshContacts: this.refreshContactsList,
            })
          }
        />

        <FlatList
          contentContainerStyle={{ flexGrow: 1 }}
          data={this.state.contacts}
          keyExtractor={(item) => item.user_id.toString()}
          renderItem={({ item }) => (
            <View style={styles.contactItem}>
              <Text style={styles.contactText} onPress={() => this.showUserProfile(item)}>
                {item.first_name} {item.last_name}
              </Text>
              <View style={styles.buttonContainer}>
                <Button title="Remove" onPress={() => this.removeContact(item.user_id)} />
                <Button title="Block" onPress={() => this.blockUser(item.user_id)} />
              </View>
            </View>
          )}
        />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingHorizontal: 10,
  },
  input: {
    padding: 10,
    borderWidth: 2,
    borderRadius: 5,
    margin: 10,
  },
  contactItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ddd',
  },
  contactText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  buttonContainer: {
    flexDirection: 'row',
  },
});

export default ContactScreen;
