import React, { Component } from 'react';
import { Text, View, Button, TextInput } from 'react-native';


class SignupScreen extends Component{
  constructor(props) {
    super(props);
    this.state = {
      user_id: '',
      first_name: '',
      last_name: '',
      email: '',
      Password: '',
      Password: '',
      isLoading: true,
    };

    //   componentDidMount(){
    //     this.getData()
    
     // }
}
    static navigationOptions = {
    header: null
    }

    addUserData(){
      return fetch("http://localhost:3333/list",
      {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
        id: this.state.id,
        Fname: this.state.first_name,
        LName: this.state.last_name,
        Email: this.state.email,
        Password: this.state.Password,
        Password: this.state.Password
        })
      })
    .then((response) => {
      if(response.status === 201){
        return response.json();
      }else if(response.status === 400){
        throw "Bad Request"
      }else {
        throw "Server Error"
      }
    })
    .then((rJson) => {
      console.log(rJson)
      this.setState({"error": "User Added Successfully"})
      this.setState({"Submitted": false});
      this.props.navigation.navigate("Login")
    })
    .catch((error) => {
      this.setState;({"error": error})
      this.setState({"Submitted": false});
    })
  }


  render(){

    const navigation = this.props.navigation;
    return(
        <View>
          <Text> To Register enter your details below</Text>


        <Text for="first_name"><b></b></Text>
        <TextInput type="text" placeholder="First Name:" name="Fname" required />


        <Text for="last_name"><b></b></Text>
        <TextInput type="text" placeholder="Last Name:" name="Lname" required />

        <Text for="email"><b></b></Text>
        <TextInput type="text" placeholder="Enter Your Email:" name="Email" required />

        <Text for="Password"><b></b></Text>
        <TextInput type="text" placeholder="Enter A Password:" name="Password" required />

        <Text for="Password"><b></b></Text>
        <TextInput type="text" placeholder="Enter Your Password Again:" name="Password" required />

        <Button
                title="Register"
                onPress={() => this.props.navigation.navigate('Login')} />

          <Button 
            title='Back'
            onPress={() => this.props.navigation.goBack()} />

        
        </View>
    );
  }
}

export default SignupScreen;
