import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import { StyleSheet, Text, View, Button, FlatList, TextInput, Alert } from 'react-native';
import { TouchableOpacity } from 'react-native';

class ContactScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contacts: [],
      searchQuery: '',
      password: '',
      modalVisible: false,
      filteredContacts: [],
    };
  }

  static navigationOptions = {
    header: null,
  };

  setModalVisible = (visible) => {
    this.setState({ modalVisible: visible });
  };

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.fetchContacts();
    });
  }

  refreshContactsList = () => {
    this.fetchContacts();
  };

  handleSearchChange = (searchQuery) => {
    this.setState({ searchQuery }, () => {
      this.filterContacts();
    });
  };

  filterContacts = () => {
    const { searchQuery, contacts } = this.state;
    const filteredContacts = contacts.filter(
      (contact) =>
        contact.first_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        contact.last_name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    this.setState({ filteredContacts });
  };

  searchUsers = async (searchQuery) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${encodeURIComponent(searchQuery)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const user = await response.json();
        return user;
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  fetchContacts = async () => {
    try {
      const response = await this.getContacts();
      this.setState({ contacts: response, filteredContacts: response });
    } catch (error) {
      console.error(error);
    }
  };

  getContacts = async () => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch('http://localhost:3333/api/1.0.0/contacts', {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
      if (response.status === 200) {
        return response.json();
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  DeleteUser = async (user_id) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/contact`, {
        method: 'DELETE',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: user_id,
        }),
      });
      if (response.status === 200) {
        this.setModalVisible(true);
        this.fetchContacts(); // fetch contacts again after successful deletion
      } else if (response.status === 400) {
        throw 'You Cant remove yourself as a contact';
      } else if (response.status === 404) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };


  blockUser = async (user_id) => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/block`, {
        method: 'POST',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
      });
  
      if (response.status === 200) {
        Alert.alert('Success', 'User blocked successfully');
        this.fetchContacts(); // fetch contacts again after successful block
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
      Alert.alert('Error', 'Failed to block user');
    }
  };

  showUserProfile = (item) => {
    console.log(item); // Debugging line
    Alert.alert(
      'User Profile',
      `Name: ${item.first_name} ${item.last_name}\nEmail: ${item.email}`,
      [{ text: 'OK', onPress: () => console.log('OK Pressed') }],
      { cancelable: false }
    );
  };

  render() {
    return (
      <View style={styles.container}>
        <TextInput
          value={this.state.searchQuery}
          onChangeText={this.handleSearchChange}
          placeholder="Type In The User's ID To Search."
          style={styles.input}
        />

        <Button
          title="Manage Contacts"
          onPress={() =>
            this.props.navigation.navigate('ManageContacts', {
              onRefreshContacts: this.refreshContactsList,
            })
          }
        />

        <FlatList
          contentContainerStyle={{ flexGrow: 1 }}
          data={this.state.filteredContacts}
          keyExtractor={(item) => item.user_id.toString()}
          renderItem={({ item }) => (
            <View style={styles.contactItem}>
              <TouchableOpacity onPress={() => this.showUserProfile(item)}>
                <Text style={styles.contactText}>
                  {item.first_name} {item.last_name}
                </Text>
              </TouchableOpacity>
              <View style={styles.buttonContainer}>
                <Button title="Remove" onPress={() => this.DeleteUser(item.user_id)} />
                <Button title="Block" onPress={() => this.blockUser(item.user_id)} />
              </View>
            </View>
          )}
        />
      </View>
    );
  }
}
