import React, { Component } from 'react';
import { Text, View, TextInput, Button, Alert } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class AddContactsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      user_id: '',
    };
  }

  AddUser = async (user_id) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/contact`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          user_id: this.state.user_id,
        }),
      });
  
      const responseText = await response.text();
  
      // if (response.status === 200 || response.status === 201) {
      //   if (responseText === "Already a contact") {
      //     alert("This user is already a contact.");
      //     return;
      //   } else {
      //     Alert.alert('Success', 'User added successfully');
      //     this.props.route.params.onRefreshContacts();
      //     this.props.navigation.goBack();
      //   }



        if (response.status === 200) {
        alert('Contact added successfully');
        this.props.route.params.onRefreshContacts();
        this.props.navigation.goBack();
      } else if (response.status === 201) {
         alert ("This user is already a contact.");  
      } else if (response.status === 400) {
        throw 'You cant add yourself as a contact';
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else if (response.status === 404) {
        throw 'Not Found';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };
  

  render() {
    return (
      <View>
        <Text>This is the Add Contacts screen</Text>
        <TextInput
          placeholder="Enter The User ID Of The Person You Want To Add"
          onChangeText={(user_id) => this.setState({ user_id })}
          value={this.state.user_id}
          style={{
            padding: 10,
            borderWidth: 2,
            borderRadius: 5,
            margin: 10,
          }}
        />

        <Button title="Add Contact" onPress={() => this.AddUser(this.state.user_id)} />
      </View>
    );
  }
}

export default AddContactsScreen;
