import React, { Component } from 'react';
import { Text, View, Image, Button, Alert } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class UpdateProfileScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        first_name: '',
        last_name: '',
        email: '',
        user_id: '',
        profilePicture: '',
      },
    };
  }


  updateUserInfo = async (newUserInfo) => {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/user/me', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorisation': await AsyncStorage.getItem('session_token'),
        },
        body: JSON.stringify(newUserInfo),
      });
  
      if (response.status === 200) {
        Alert.alert('Success', 'User info updated successfully');
      } else {
        throw new Error('Failed to update user info');
      }
    } catch (error) {
      Alert.alert('Error', error.toString());
    }
  };
  

  render() {
    const { user } = this.state;

    return (
      <View>
      </View>
    );
  }
}

export default UpdateProfileScreen;
