import React, { Component } from 'react';
import { TextInput, Button, Alert, View } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class NewChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newChatUserId: '',
    };
  }

  handleChatIdInputChange = (text) => {
    this.setState({ newChatUserId: text });
  }

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.fetchContacts();
    });
  }

  refreshContactsList = () => {
    this.fetchContacts();
  };

  handleSearchChange = (searchQuery) => {
    this.setState({ searchQuery });
  };

  handleSearch = async () => {
    if (this.state.searchQuery) {
      const user = await this.searchUsers(this.state.searchQuery);
      this.setState({ contacts: user ? [user] : [] });
    } else {
      this.fetchContacts();
    }
  };

  searchUsers = async (searchQuery) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${encodeURIComponent(searchQuery)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const user = await response.json();
        return user;
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  fetchContacts = async () => {
    try {
      const response = await this.getContacts();
      this.setState({ contacts: response });
    } catch (error) {
      console.error(error);
    }
  };

  getContacts = async () => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch('http://localhost:3333/api/1.0.0/contacts', {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        return response.json();
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  async startConversation(userId) {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      
      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'POST',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: userId }),
      });
  
      console.log("Response: ", response);
  
      if (response.status === 201) {
        const chatDetails = await response.json();
        console.log("Chat Details: ", chatDetails);
        return chatDetails;  
      } else if (response.status === 400) {
        throw 'Bad Request';
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.log("Error in startConversation: ", error);
      Alert.alert('Error', error.toString());
    }
  }

  createNewChat = async () => {
    const { newChatUserId } = this.state;

    if (newChatUserId) {
      try {
        console.log("Attempting to start conversation...");
        const chatDetails = await this.startConversation(newChatUserId);
        Alert.alert('Success', 'New chat created successfully');
      } catch (error) {
        console.log("Failed to start conversation: ", error);
        Alert.alert('Error', error.toString());
      }
    } else {
      Alert.alert('Error', 'Please input User ID');
    }
  };

  async AddUserToChat(chatId, userId) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/users/${userId}', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
      
      if (response.status === 200) {
          const chatDetails = await response.json();
          this.setState({ chatDetails: chatDetails });
      } else if (response.status === 400) {
          console.log('Bad Request');
      } else if (response.status === 401) {
          console.log('Unathorised');
          await AsyncStorage.removeItem('whatsthat_session_token');
          await AsyncStorage.removeItem('whatsthat_user_id');
          this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
          console.log('Forbidden');
      } else if (response.status === 404) {
          console.log('Not Found');
      } else {
          throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }

  
  render() {
    const { newChatUserId } = this.state;

    return (
      <View>
        <TextInput
          placeholder="Enter user ID"
          value={newChatUserId}
          onChangeText={this.handleChatIdInputChange}
        />
        <Button title="Create Chat" onPress={this.createNewChat} />
      </View>
    );
  }
}

export default NewChatScreen;
