import React, { Component } from 'react';
import { Text, View, Button, TextInput } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';


class LoginScreen extends Component{
  constructor(props) {
    super(props);
    this.state = {
      user_id: '',
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      isLoading: true,
    };

  }
  
    static navigationOptions = {
      header: null
      }

             // Helper function to check if email is valid
             isValidEmail(email) {
              const check = /\S+@\S+\.\S+/;
              return check.test(email);
            }
            
            // Helper function to check if password is valid
             isValidPassword(password) {
              const check = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9]{8,}$/;
              return check.test(password);
            }


      async Login() {
        console.log("inside login", this.state.email, this.state.password)
        if (!this.state.email || !this.state.password) {
          this.setState({ error: "Please enter both email and password." });
          return;
        }
        if (!isValidEmail(this.state.email)) {
          this.setState({ error: "Please enter a valid email address." });
          return;
        }
        if (!isValidPassword(this.state.password)) {
          this.setState({ error: "Password must be at least 8 characters long and contain at least one uppercase letter, one lowercase letter, and one number." });
          return;
        }
      
        const toSend ={
          email: this.state.email,
          password: this.state.password
        } 
        return fetch("http://localhost:3333/api/1.0.0/login", {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          'x-Authorization':'whatsthat_session_token',
          body: JSON.stringify({
      toSend
          })
        })
          .then((response) => {
            console.log("login requests sent")
            if (response.status === 201) {
              console.log(response)
              return response.json();
            } else if (response.status === 400) {
              throw "Invalid Email Or Password Entered"
            } else {
              throw "Server Error"
            }
          })
      
          .then(async (rJson) => {
            console.log(rJson)
            try {
              await AsyncStorage.setItem("whatsthat_user_id", rJson.id)
              await AsyncStorage.setItem("whatsthat_session_token", rJson.token)
      
              this.setState({ "Submitted": false });
      
              this.props.navigation.navigate("Main")
            } catch {
              throw "Server Error"
            }
          })
          .catch((error) => {
          
          console.log(error);
          })
          

          .then((rJson) => {
            console.log(rJson)
            this.setState({ "error": "User Added Successfully" })
            this.setState({ "Submitted": false });
            this.props.navigation.navigate("Login")
          })
      
      }
      




  render(){

    const navigation = this.props.navigation;
    return(
      
        <View style={{ flex: 1}}>
          <Text 
          style={{fontSize: 20, padding:10}}>
            Please sign in to continue.
          </Text>


      <TextInput 

    type="text" placeholder="Enter Email:" name="Email" required 
    onChangeText={(c) => this.setState({ email: c})}
    style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}

  // UnlockIcon={<Icon name='unlock' size={30} color='black' />}
/>


  <TextInput
  secureTextEntry={true} placeholder="Enter Password" name="Password" required 
  onChangeText={(c) => this.setState({ password: c})}
  style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}


    LockIcon={<Icon name='lock' size={30} color='black' />}

  />

{/* MAKE THE BUTTONS SMALLER AND ADD COLOUR */}
      
    <Button
      title="Log In"
      onPress={this.Login}
      
    />

    <Button
      title="Sign Up"
      onPress={() => this.props.navigation.navigate('Signup')}
    />

</View>


    );
  }

  

}

export default LoginScreen;
