import React, { Component } from 'react';
import { Text, View, TextInput, Button, Modal, TouchableOpacity, FlatList } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class AddContactsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      user_id: '',
      modalVisible: false,
      errorMessage: '',
      searchQuery: '',
      availableUsers: [],
      addedUsers: [], 
      first_name: '', 
      last_name: '',
    };
  }

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', async () => {
      await this.fetchContacts(); // await ensures that this operation completes before proceeding
      this.getAllUsers();
    });
  };

  setModalVisible = (visible, message) => {
    this.setState({ modalVisible: visible, errorMessage: message });
  };

  handleSearchChange = (searchQuery) => {
    this.setState({ searchQuery });
  };

  handleSearch = async () => {
    if (this.state.searchQuery) {
        const user = await this.searchUsers(this.state.searchQuery);
        this.setState({ availableUsers: user ? [user] : [] });
    } else {
        this.getAllUsers();
    }
};

  handleAddUser = (user_id) => {
    this.AddUser(user_id);
  };

  AddUser = async (user_id) => {
    try {
      const currentUserId = await AsyncStorage.getItem('whatsthat_user_id');
      if (currentUserId === user_id) {
        this.setModalVisible(true, "You can't add yourself as a contact");
        return;
      }
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/contact`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          user_id: user_id,
      }),      
      });

      if (response.status === 200) {
        //navigation.navigate('AddContactsScreen', { onRefreshContacts: this.yourRefreshFunction });
        this.setModalVisible(true, 'Contact added successfully');
      } else if (response.status === 400) {
        this.setModalVisible(true, "You Can't Add Yourself As A Contact.");
      } else if (response.status === 401) {
        this.setModalVisible(true, "Unauthorised");
      } else if (response.status === 404) {
        this.setModalVisible(true, "Not Found");
      } else if (response.status === 409) {
        this.setModalVisible(true, "This user is already a contact.");      
      } else {
        this.setModalVisible(true, "Server Error");
      }
    } catch (error) {
      console.error(error);
    }
  };
  searchUsers = async (searchQuery) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${encodeURIComponent(searchQuery)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
      if (response.status === 200) {
        const user = await response.json();
        return user;
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  getAllUsers = async () => {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/search', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
  
      if (response.status === 200) {
        const responseData = await response.json();
        const users = responseData.filter(user => 
        (!this.state.addedUsers || !this.state.addedUsers.some(addedUser => addedUser.user_id === user.user_id)));
        this.setState({ availableUsers: users });
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };
  

  fetchContacts = async () => {
    try {
      const response = await this.getContacts();
      this.setState({ contacts: response, filteredContacts: response });
    } catch (error) {
      console.error(error);
    }
  };

  getContacts = async () => {
    try {
      const token = await AsyncStorage.getItem('whatsthat_session_token');
      const response = await fetch('http://localhost:3333/api/1.0.0/contacts', {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
      if (response.status === 200) {
        const addedUsers = await response.json();
        this.setState({ addedUsers });  // update addedUsers state
        return addedUsers;   // return the already parsed JSON      
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };
  
  
  

render() {
  const { modalVisible, errorMessage, searchQuery, availableUsers } = this.state;

  return (
    <View>
      <TextInput
        placeholder="Search users by ID"
        onChangeText={this.handleSearchChange}
        value={searchQuery}
        style={{
          padding: 10,
          borderWidth: 2,
          borderRadius: 5,
          margin: 10,
        }}
      />

      <Button title="Search" onPress={this.handleSearch} />

      <FlatList
        data={availableUsers}
        keyExtractor={(item) => item.user_id.toString()}
        renderItem={({ item }) => (
          <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 10 }}>
             <Text>{item.given_name} {item.family_name}</Text>
            <Button title="Add" onPress={() => this.handleAddUser(item.user_id)} />
          </View>
        )}
      />

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          this.setModalVisible(false);
        }}
      >
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <View style={{ backgroundColor: 'white', padding: 20, borderRadius: 10 }}>
            <Text>{errorMessage}</Text>
            <TouchableOpacity
              style={{ backgroundColor: 'blue', padding: 10, borderRadius: 5, marginTop: 10 }}
              onPress={() => {
                this.setModalVisible(false);
                if (errorMessage === 'Contact added successfully') {
                }
              }}
            >
              <Text style={{ color: 'white' }}>Close</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
}

}

export default AddContactsScreen;
