import React, { Component } from 'react';
import { Text, View, TextInput, Button, Alert, Image, StyleSheet, Modal } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class EditProfileScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        first_name: '',
        last_name: '',
        email: '',
        password: '',
        oldPassword: '',
        profilePicture: null
      },
      isLoading: false,
      successMessage: '',
      errorMessage: '',
      showModal: false
    };
  }

  componentDidMount() {
    this.getCurrentUserInfo();
    this.GPI();
  }

  handleInputChange = (field, value) => {
    this.setState((prevState) => ({
      user: {
        ...prevState.user,
        [field]: value,
      },
    }));
  };

  
  navigateToCamera = () => {
    this.props.navigation.navigate('Camera');
  };

  showSuccessModal = (message) => {
    this.setState({
      successMessage: message,
      errorMessage: '',
      showModal: true
    });
  };

  showErrorModal = (message) => {
    this.setState({
      successMessage: '',
      errorMessage: message,
      showModal: true
    });
  };

  closeSuccessModal = () => {
    this.setState({
      successMessage: '',
      showModal: false
    });
    this.props.navigation.navigate('Profile'); 
  };

  closeErrorModal = () => {
    this.setState({
      errorMessage: '',
      showModal: false
    });
  };


  getCurrentUserInfo = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const user = await response.json();
        this.setState({ user });
      } else if (response.status === 401) {
        throw new Error('Unauthorised');
      } else if (response.status === 404) {
        throw 'Not Found';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.showErrorModal(error.toString());
    }
  };

  updateUserInfo = async () => {
    const { user } = this.state;
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');  
    let data = {};
    if(user.first_name) data.first_name = user.first_name;
    if(user.last_name) data.last_name = user.last_name;
    if(user.email) data.email = user.email;
    if(user.oldPassword && user.password) {
      data.old_password = user.oldPassword;
      data.new_password = user.password;
    }
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify(data),
      });
      if (response.status === 200) {
        this.showSuccessModal('User info updated successfully');
        await this.getCurrentUserInfo();
      } else if (response.status === 400) {
        throw new Error('Bad Request');
      } else if (response.status === 401) {
        throw new Error('Unauthorised');
      } else if (response.status === 403) {
        throw 'Forbidden';
      } else if (response.status === 404) {
        throw 'Not Found';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.showErrorModal(error.toString());
    }
  };

  GPI = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');

    try {
      let response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/photo`, {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        }
      });

      let resBlob = await response.blob();
      let data = URL.createObjectURL(resBlob);

      this.setState(prevState => ({
        user: {
          ...prevState.user,
          profilePicture: data
        },
        isLoading: false
      }));
    } catch(error) {
      this.showErrorModal(error.toString());
    }
  };


  render() {
    const { user, successMessage, errorMessage, showModal } = this.state;

    return (
      <View style={styles.container}>
        <Image source={{ uri: user.profilePicture }} style={styles.profilePicture} />
        <Button title="Go to Camera" onPress={this.navigateToCamera} />

        <Text style={styles.label}>First Name:</Text>
        <TextInput
          style={styles.input}
          value={user.first_name || ''}
          onChangeText={(text) => this.handleInputChange('first_name', text)}
        />

        <Text style={styles.label}>Last Name:</Text>
        <TextInput
          style={styles.input}
          value={user.last_name || ''}
          onChangeText={(text) => this.handleInputChange('last_name', text)}
        />

        <Text style={styles.label}>Email:</Text>
        <TextInput
          style={styles.input}
          value={user.email || ''}
          onChangeText={(text) => this.handleInputChange('email', text)}
        />

        <Text style={styles.label}>Old Password:</Text>
        <TextInput
          style={styles.input}
          secureTextEntry={true}
          value={user.oldPassword || ''}
          onChangeText={(text) => this.handleInputChange('oldPassword', text)}
        />

        <Text style={styles.label}>New Password:</Text>
        <TextInput
          style={styles.input}
          secureTextEntry={true}
          value={user.password || ''}
          onChangeText={(text) => this.handleInputChange('password', text)}
        />

         <Button title="Update Info"  onPress={async () => {await this.updateUserInfo(); }}
        />
 
        <Modal visible={showModal} animationType="fade" transparent={true}>
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <Text style={styles.modalMessage}>{successMessage || errorMessage}</Text>
              <Button title="Close" onPress={successMessage ? this.closeSuccessModal : this.closeErrorModal} />
            </View>
          </View>
        </Modal>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    alignItems: 'center',
    justifyContent: 'center',
  },
  profilePicture: {
    width: 100,
    height: 100,
    marginBottom: 16,
    borderRadius: 50,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  input: {
    height: 40,
    width: '100%',
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 16,
    paddingHorizontal: 8,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalContent: {
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  modalMessage: {
    fontSize: 18,
    marginBottom: 10,
  },
});

export default EditProfileScreen;
