import React, { Component } from 'react';
import { Text, View, Button, TextInput } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';




class LoginScreen extends Component{
  constructor(props) {
    super(props);
    this.state = {
      user_id: '',
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      isLoading: true,
    };

  }
  
    static navigationOptions = {
      header: null
      }
  
      Login(){
        return fetch("http://localhost:3333/list",
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
          "email": this.state.email,
          "password": this.state.password
          })
        })
  
    .then((response) => {
      if(response.status === 201){
        return response.json();
      }else if(response.status === 400){
        throw "Invalid Email Or Password Entered"
      }else {
        throw "Server Error"
      }
    })

    .then((rJson) => {
      console.log(rJson)
      this.setState({"error": "User Added Successfully"})
      this.setState({"Submitted": false});
      this.props.navigation.navigate("Login")
    })

    .catch((error) => {
      this.setState;({"error": error})
      this.setState({"Submitted": false});
    })
    .then(async (rJson) => {
      console.log(rJson)
      try{
        await AsyncStorage.setItem("whatsthat_user_id", rJson.id)
        await AsyncStorage.setItem("whatsthat_session_token", rJson.token)

        this.setState({"Submitted": false});

        this.props.navigate("MainScreen")
      }catch{
        throw "Server Error"
      }
    })
    
  }

  render(){

    const navigation = this.props.navigation;
    return(
      
        <View style={{ flex: 1}}>
          <Text style={{fontSize: 20}}>Please sign in to continue.</Text>

          <Text for="Email"><b></b></Text>
    <TextInput type="text" placeholder="Enter Email:" name="Email" required style={{ borderWidth: 1, borderColor: 'black', borderRadius: 5, paddingLeft: 10, height: 40, width: '100%', marginTop: 10 }}/>


  <TextInput
  secureTextEntry={true}
  placeholder="Enter Password"
  name="Password"
  required
  style={{ borderWidth: 1, borderColor: 'black', borderRadius: 5, paddingLeft: 10, height: 40, width: '100%', marginTop: 10 }}
  leftIcon={<Icon name='lock' size={30} color='black' />}
  />


<TextInput
  secureTextEntry={true}
  type="text"
  placeholder="Enter Password:"
  name="Password"
  required
  leftIcon={ <Icon name='lock' size={30} color='black'/> }
/>


        
        <Button
            title="Log In"
            onPress={() => this.Login('Main')} />
{/* MAKE THE BUTTONS SMALLER AND COLOUR */}
<Button
    title="Sign Up"
    onPress={() => this.props.navigation.navigate('Signup')}
  />


        </View>
    );
  }

  

}

export default LoginScreen;
