import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import { Text, View, Button, FlatList, TextInput } from 'react-native';

class ContactScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contacts: [],
      searchQuery: '',
    };
  }

  static navigationOptions = {
    header: null,
  };

  componentDidMount() {
    this.fetchContacts();
  }

  refreshContactsList = () => {
    this.fetchContacts();
  };

  handleSearchChange = (searchQuery) => {
    this.setState({ searchQuery });
  };

  handleSearch = async () => {
    if (this.state.searchQuery) {
      const user = await this.searchUsers(this.state.searchQuery);
      this.setState({ contacts: user ? [user] : [] });
    } else {
      this.fetchContacts();
    }
  };

  searchUsers = async (searchQuery) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${encodeURIComponent(searchQuery)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
    
      if (response.status === 200) {
        const user = await response.json();
        return user;
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };

  async fetchContacts() {
    try {
      const response = await this.GetContacts();
      this.setState({ contacts: response });
    } catch (error) {
      console.error(error);
    }
  }

  async GetContacts() {
    return fetch('http://localhost:3333/api/1.0.0//contacts', {
      method: 'GET',
      headers: {
        'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else if (response.status === 401) {
          throw 'Unauthorised';
        } else {
          throw 'Server Error';
        }
      });
  }

  render() {
    return (
      <View style={{ flex: 1 }}>

<View style={styles.textBox}>
  <TextInput
    value={this.state.searchQuery}
    onChangeText={this.handleSearchChange}
    placeholder="Enter search text..."
    style={styles.textInput}
  />
</View>

        <Button title="Search" onPress={this.handleSearch} />

        <Button title="Add Contact" onPress={() => this.props.navigation.navigate('AddContact', {
              onRefreshContacts: this.refreshContactsList,
            })
          }
        />

        <Button title="Delete Contact" onPress={() => this.props.navigation.navigate('DeleteContact', {
              onRefreshContacts: this.refreshContactsList,
            })
          }
        />

        <FlatList
          contentContainerStyle={{ flexGrow: 1 }}
          data={this.state.contacts}
          keyExtractor={(item) => item.user_id.toString()}
          renderItem={({ item }) => (
            <View>
              <Text>{item.first_name} {item.last_name}</Text>
              <Text>{item.email}</Text>
              <Text>{item.user_id}</Text>
            </View>
          )}
        />
      </View>
    );
  }
}

export default ContactScreen;
