import React, { Component } from 'react';
import { Text, View, TextInput, Button, Alert, Image } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class UpdateProfileScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        first_name: '',
        last_name: '',
        email: '',
        password: '',
        oldPassword: '',
        profilePicture: null
      },
      isLoading: false
    };
  }

  componentDidMount() {
    this.getCurrentUserInfo();
    this.GPI();
  }

  getCurrentUserInfo = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
    const session_token = await AsyncStorage.getItem('whatsthat_session_token');
    console.log("Session token:", session_token);

    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const user = await response.json();
        this.setState({ user });
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else if (response.status === 404) {
        throw 'Not Found';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      Alert.alert('Error', error.toString());
    }
  };


  handleInputChange = (field, value) => {
    this.setState((prevState) => ({
      user: {
        ...prevState.user,
        [field]: value,
      },
    }));
  };

  updateUserInfo = async () => {
    const { user } = this.state;
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');

    // Prepare data to send in the PATCH request
    const data = {
      first_name: user.first_name,
      last_name: user.last_name,
      email: user.email,
      old_password: user.oldPassword,
      new_password: user.password
    };

    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify(data), // Send the prepared data
      });

      if (response.status === 200) {
        Alert.alert('Success', 'User info updated successfully');
      } else if (response.status === 401) {
        throw new Error('Unauthorised');
      } else {
        throw new Error('Failed to update user info');
      }
    } catch (error) {
      Alert.alert('Error', error.toString());
    }
  };

  // uploadProfilePhoto = async (photoUri) => {
  //   let photo = {
  //     uri: photoUri,
  //     type: 'image/jpeg',
  //     name: 'profile_photo.jpg',
  //   };
  
  //   let formData = new FormData();
  //   formData.append('photo', photo);
  
  //   try {
  //     const response = await fetch('http://localhost:3333/api/1.0.0/user//profile-photo', {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'multipart/form-data',
  //         'X-Authorisation': await AsyncStorage.getItem('session_token'),
  //       },
  //       body: formData,
  //     });
  
  //     if (response.status === 200) {
  //       Alert.alert('Success', 'Profile photo uploaded successfully');
  //     } else {
  //       throw new Error('Failed to upload profile photo');
  //     }
  //   } catch (error) {
  //     Alert.alert('Error', error.toString());
  //   }
  // };

  GPI = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
    
    try {
      let response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/photo`, {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        }
      });
  
      let resBlob = await response.blob();
      let data = URL.createObjectURL(resBlob);
  
      this.setState(prevState => ({
        user: {
          ...prevState.user,
          profilePicture: data
        },
        isLoading: false
      }));
    } catch(err) {
      console.log("error", err);
    }
  };
  
  deletePhoto = async () => {
    const user_id = await AsyncStorage.getItem('whatsthat_user_id');
    
    try {
      let response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/photo`, {
        method: 'DELETE',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        }
      });

      if(response.status === 200) {
        Alert.alert("Success", "Profile photo deleted successfully");
        // Once the photo is deleted, remove it from state as well
        this.setState(prevState => ({
          user: {
            ...prevState.user,
            profilePicture: null
          }
        }));
      } else {
        throw new Error('Failed to delete profile photo');
      }
    } catch(err) {
      console.log("error", err);
      Alert.alert("Error", err.toString());
    }
  };

  navigateToCamera = () => {
    this.props.navigation.navigate('Camera');
  };






  render() {
    const { user } = this.state;

    return (
      <View>


      <Image source={{ uri: user.profilePicture }} style={{ width: 100, height: 100 }} />
        <Button title="Delete Photo" onPress={this.deletePhoto} />
        <Button title="Go to Camera" onPress={this.navigateToCamera} />
     

        <Text>First Name:</Text>
        <TextInput
          value={user.first_name || ''}
          onChangeText={(text) => this.handleInputChange('first_name', text)}
        />

        <Text>Last Name:</Text>
        <TextInput
          value={user.last_name || ''}
          onChangeText={(text) => this.handleInputChange('last_name', text)}
        />

        <Text>Email:</Text>
        <TextInput
          value={user.email || ''}
          onChangeText={(text) => this.handleInputChange('email', text)}
        />

        <Text>Old Password:</Text>
        <TextInput
          secureTextEntry
          value={user.oldPassword || ''}
          onChangeText={(text) => this.handleInputChange('oldPassword', text)}
        />

        <Text>New Password:</Text>
        <TextInput
          secureTextEntry
          value={user.password || ''}
          onChangeText={(text) => this.handleInputChange('password', text)}

      />

      <Button title="Update Info" onPress={this.updateUserInfo} />
    </View>
  );
 }
}

export default UpdateProfileScreen;
