import React, { Component } from 'react';
import { ScrollView, TextInput, Button, View, Text, StyleSheet, Alert, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class SingleChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      singleChat: null,
      error: null,
      newMessage: '',
      messages: [
        // { id: 1, sender: 'User1', message: 'Hi there!' },
        // { id: 2, sender: 'User2', message: 'Hello!' },
        // { id: 3, sender: 'User1', message: 'How are you?' },
        // { id: 4, sender: 'User2', message: 'I\'m good. Thanks!' },
        // { id: 5, sender: 'User1', message: 'I\'m good. Thanks!' },
        // { id: 6, sender: 'User2', message: 'I\'m good. Thanks!' },
        // { id: 7, sender: 'User1', message: 'I\'m good. Thanks!' },
        // { id: 8, sender: 'User2', message: 'I\'m good. Thanks!' },
     
      ]
    };
  }

  componentDidMount() {
    const { route, navigation } = this.props;
    const { chat_id } = route.params;
    this.viewSingleChat(chat_id);
    this.setState({ currentchat_id: chat_id });
    navigation.setOptions({ 
      title: chat_id, 
      headerRight: () => (
        <TouchableOpacity onPress={() => navigation.navigate('UpdateChat', { chat_id })}>
          <Text style={{ color: 'blue', marginRight: 10, fontWeight: 'bold', textDecorationLine: 'underline', fontSize: 15 }}>
            Edit Chat</Text>
        </TouchableOpacity>
      )
    });
  }
  
  
  
  handleMessageInputChange = (text) => {
    this.setState({ newMessage: text });
  }

  sendMessage = async () => {
    if (this.state.newMessage && this.state.currentchat_id) {
      try {
        await this.SendMessage(this.state.currentchat_id, this.state.newMessage);
        this.setState({ newMessage: '' });
        Alert.alert('Success', 'Message sent successfully');

        // Fetch chat details again to update the messages
        this.viewSingleChat(this.state.currentchat_id);
      } catch (error) {
        Alert.alert('Error', error.toString());
      }
    }
  };


  updateMessage = async () => {
    if (this.state.newMessage && this.state.currentchat_id && this.state.currentmessage_id) {
      try {
        await this.UpdateMessage(this.state.currentchat_id, this.state.currentmessage_id, this.state.newMessage);
        this.setState({ newMessage: '', editingMessage: false, currentmessage_id: null });
        Alert.alert('Success', 'Message updated successfully');
      } catch (error) {
        Alert.alert('Error', error.toString());
      }
    }
  };

  async viewSingleChat(chat_id) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}`, {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
      });
      if (response.status === 200) {
        const chat = await response.json();
        this.setState({ 
          singleChat: chat,
          messages: chat.messages || [] // Update the messages state here
        });
        this.props.navigation.setOptions({ title: chat.name });
      } else if (response.status === 401) {
        console.log('Unauthorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }


  async SendMessage(chat_id, message) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          message: message,
        }),
      });
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ 
          chatDetails: chatDetails, 
          messages: chatDetails.messages || []  // Update messages
        });
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }
  
  async UpdateMessage(chat_id, message_id, message) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}/message/${message_id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          message: message,
        }),
      });
  
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  } 
 

async DeleteMessage(chat_id, message_id) {
  try {
    const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}/message/${message_id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    });

    if (response.status === 200) {
      const chatDetails = await response.json();
      this.setState({ chatDetails: chatDetails });
    } else if (response.status === 401) {
      console.log('Unathorised');
      await AsyncStorage.removeItem('whatsthat_session_token');
      await AsyncStorage.removeItem('whatsthat_user_id');
      this.props.navigation.navigate('Login');
    } else if (response.status === 403) {
      console.log('Forbidden');
    } else if (response.status === 404) {
      console.log('Not Found');
    } else {
      throw 'Server Error';
    }
  } catch (error) {
    this.setState({ error: error });
  }
}

render() {
    const { singleChat, messages } = this.state;

    if (!singleChat) {
      return <Text>Loading...</Text>;
    }

    return (
      <View style={styles.container}>
        <ScrollView style={styles.messagesContainer}>
        {messages.map((msg, index) => (
  <View key={msg.id} style={[
      styles.messageBox,
      msg.sender === this.state.currentUser ? styles.rightAlign : styles.leftAlign
  ]}>
    <Text style={styles.messageSender}>{msg.sender}</Text>
    <Text style={styles.messageText}>{msg.message}</Text>
  </View>
))}

        </ScrollView>
        <View style={styles.inputContainer}>
          <TextInput 
            style={styles.input}
            placeholder="Type your message here..."
            value={this.state.newMessage}
            onChangeText={this.handleMessageInputChange}
          />
          <Button 
            title="Send"
            onPress={this.sendMessage} 
          />
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  messagesContainer: {
    flex: 1,
    padding: 10,
  },
  messageBox: {
    marginVertical: 10,
    padding: 10,
    borderRadius: 5,
    borderColor: '#ccc',
    borderWidth: 1,
  },
  rightAlign: {
    alignSelf: 'flex-end',
    backgroundColor: '#dcf8c6',
  },
  leftAlign: {
    alignSelf: 'flex-start',
    backgroundColor: '#fff',
  },
  messageSender: {
    fontWeight: 'bold',
    marginBottom: 5,
  },
  messageText: {
    fontSize: 16,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 10,
    borderTopColor: '#ccc',
    borderTopWidth: 1,
  },
  input: {
    flex: 1,
    marginRight: 10,
    height: 40,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 5,
    padding: 5,
  },
});

export default SingleChatScreen;
