import React, { Component } from 'react';
import { Text, View, TextInput, Button, Alert, Modal, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { FlatList } from 'react-native-web';

class AddContactsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      user_id: '',
      modalVisible: false,
    };
  }

  setModalVisible = (visible) => {
    this.setState({ modalVisible: visible });
  };

  AddUser = async (user_id) => {
    try {
      const currentUserId = await AsyncStorage.getItem('whatsthat_user_id');
  
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/contact`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          user_id: this.state.user_id,
        }),
      });
  
      if (response.status === 200) {
        this.props.route.params.onRefreshContacts();
        this.setModalVisible(true);
      } else if (response.status === 201) {
        alert("This user is already a contact.");
      } else if (response.status === 400) {
        throw 'You cant add yourself as a contact';
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else if (response.status === 404) {
        throw 'Not Found';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      console.error(error);
    }
  };
  

  render() {
    const { modalVisible } = this.state;

    return (
      <View>
        <FlatList>
        <Text>This is the Add Contacts screen</Text>
        <TextInput
          placeholder="Enter The User ID Of The Person You Want To Add"
          onChangeText={(user_id) => this.setState({ user_id })}
          value={this.state.user_id}
          style={{
            padding: 10,
            borderWidth: 2,
            borderRadius: 5,
            margin: 10,
          }}
        />

        <Button title="Add Contact" onPress={() => {
          this.AddUser(this.state.user_id);
          this.setModalVisible(true);
        }} />

        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => {
            this.setModalVisible(!modalVisible);
          }}
        >
          <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <View style={{ backgroundColor: 'white', padding: 20, borderRadius: 10 }}>
              <Text>Contact added successfully</Text>
              <TouchableOpacity
                style={{ backgroundColor: 'blue', padding: 10, borderRadius: 5, marginTop: 10 }}
                onPress={() => {
                  this.setModalVisible(false);
                  this.props.navigation.goBack();
                }}
              >
                <Text style={{ color: 'white' }}>Close</Text>
             

              </TouchableOpacity>
            </View>
          </View>
        </Modal>
        </FlatList>
      </View>
    );
  }
}

export default AddContactsScreen;
