import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { Component } from 'react';
import { Text, View, Button, FlatList } from 'react-native';

class ContactScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contacts: [],
    };
  }

  static navigationOptions = {
    header: null
  }

  componentDidMount() {
    this.fetchContacts();
  }
  
  async fetchContacts() {
    try {
      const response = await this.GetContacts();
      this.setState({ contacts: response }, () => {
        console.log('Contacts in state:', this.state.contacts);
      });
    } catch (error) {
      console.error(error);
    }
  }
  

  async GetContacts() {
    return fetch("http://localhost:3333/api/1.0.0/user/contacts", {
      method: "GET",
      headers: {
        "X-Authorization": `Bearer ${await AsyncStorage.getItem("whatsthat_user_id")}`,
      }
      
    })
    .then(async (response) => {
      console.log('Response status:', response.status);
      console.log('Response headers:', response.headers);
      const jsonResponse = await response.json();
      console.log('Response JSON:', jsonResponse);
      
      if (response.status === 200) {
        return jsonResponse;
      } else if (response.status === 401) {
        throw "Unauthorised";
      } else {
        throw "Server Error";
      }
    });
  }
  
  

  renderItem = ({ item }) => (
    <View>
      <Text>{`User ID: ${item.user_id}`}</Text>
      <Text>{`First Name: ${item.first_name}`}</Text>
      <Text>{`Last Name: ${item.last_name}`}</Text>
      <Text>{`Email: ${item.email}`}</Text>
    </View>
  );

  render() {
    return (
      <View>
        <Text>Contact Screen</Text>
        
        <Button
          title="Add Contact"
          onPress={() => this.props.navigation.navigate('AddContact')}
        />

        <Button
          title="Delete Contact"
          onPress={() => this.props.navigation.navigate('DeleteContact')}
        />

        <FlatList
          data={this.state.contacts}
          renderItem={this.renderItem}
          keyExtractor={(item) => item.user_id.toString()}
        />
      </View>
    );
  }
}

export default ContactScreen;
