import React, { Component } from 'react';
import { Text, View, TextInput, Button, FlatList, AsyncStorage, item, TouchableOpacity } from 'react-native';

class ChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      error: null,
    };
  }

  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.viewChats();
    });
  }
  
  componentWillUnmount() {
    if (this.focusListener != null && this.focusListener.remove) {
      this.focusListener.remove();
    }
  }
  
  async viewChats() {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
      });
      if (response.status === 200) {
        const chats = await response.json();
        this.setState({ chats: chats });
        console.log(this.state.chats);
      } else if (response.status === 401) {
        console.log('Unauthorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }

  render() {
    const { chats } = this.state;
  
    const renderItem = ({ item }) => {
      return (
        <View>
          <Text>Chat ID: {item.chat_id}</Text>
          <Text>Chat Name: {item.name}</Text>
          <Text>Creator: {item.creator.first_name} {item.creator.last_name}</Text>
          {/* <Text>Last Message: {item.last_message.message}</Text> */}
        </View>
      );
    };    
  
    return (
      <View>
        <TouchableOpacity 
          style={{ alignSelf: 'center', margin: 10, padding: 10, backgroundColor: '#007AFF' }} 
          onPress={() => this.props.navigation.navigate('NewChat')}
        >
          <Text style={{ color: '#fff' }}>Create New Chat</Text>
        </TouchableOpacity>
  
        <FlatList
          data={chats}
          renderItem={renderItem}
          keyExtractor={(item) => item.chat_id.toString()}
        />
      </View>
    );
  }
}

export default ChatScreen;
