import React, { Component } from 'react';
import { Text, View } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class SingleChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      singleChat: null,
      error: null,
    };
  }

  componentDidMount() {
    const { route, navigation } = this.props;
    const { chat_id } = route.params;
    this.viewSingleChat(chat_id);
    this.setState({ currentChatId: chat_id });
    navigation.setOptions({ title: chat_id });
  }
  
  handleMessageInputChange = (text) => {
    this.setState({ newMessage: text });
  }


  sendMessage = async () => {
    if (this.state.newMessage && this.state.currentChatId) {
      try {
        await this.SendMessage(this.state.currentChatId, this.state.newMessage);
        this.setState({ newMessage: '' });
        Alert.alert('Success', 'Message sent successfully');
      } catch (error) {
        Alert.alert('Error', error.toString());
      }
    }
  };

  updateMessage = async () => {
    if (this.state.newMessage && this.state.currentChatId && this.state.currentMessageId) {
      try {
        await this.UpdateMessage(this.state.currentChatId, this.state.currentMessageId, this.state.newMessage);
        this.setState({ newMessage: '', editingMessage: false, currentMessageId: null });
        Alert.alert('Success', 'Message updated successfully');
      } catch (error) {
        Alert.alert('Error', error.toString());
      }
    }
  };

  
  async viewSingleChat(chat_id) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}`, {
        method: 'GET',
        headers: {
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
      });
      if (response.status === 200) {
        const chat = await response.json();
        this.setState({ singleChat: chat });
        this.props.navigation.setOptions({ title: chat.name });
      } else if (response.status === 401) {
        console.log('Unauthorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }


  async SendMessage(chatId, message) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/messages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          message: message,
        }),
      });
  
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }
  
  async UpdateMessage(chatId, messageId, message) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/messages/${messageId}', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify({
          message: message,
        }),
      });
  
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  } 
 

async DeleteMessage(chatId, messageId) {
  try {
    const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/messages/${messageId}', {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    });

    if (response.status === 200) {
      const chatDetails = await response.json();
      this.setState({ chatDetails: chatDetails });
    } else if (response.status === 401) {
      console.log('Unathorised');
      await AsyncStorage.removeItem('whatsthat_session_token');
      await AsyncStorage.removeItem('whatsthat_user_id');
      this.props.navigation.navigate('Login');
    } else if (response.status === 403) {
      console.log('Forbidden');
    } else if (response.status === 404) {
      console.log('Not Found');
    } else {
      throw 'Server Error';
    }
  } catch (error) {
    this.setState({ error: error });
  }
}





  render() {
    const { singleChat } = this.state;
    
    if (!singleChat) {
      return <Text>Loading...</Text>;
    }

    return (
      <View>
        <Text>Chat ID: {singleChat.chat_id}</Text>
        <Text>Chat Name: {singleChat.name}</Text>
        <Text>Creator: {singleChat.creator.first_name} {singleChat.creator.last_name}</Text>
        <Text>Last Message: {singleChat.last_message ? singleChat.last_message.message : 'No messages yet'}</Text>

        <TextInput 
          placeholder="Type your message here..."
          value={this.state.newMessage}
          onChangeText={this.handleMessageInputChange}
        />
        {this.state.editingMessage ?
          <Button title="Update message" onPress={this.updateMessage} />
          :
          <Button title="Send message" onPress={this.sendMessage} />
        }
      </View>
    );
  }
}

export default SingleChatScreen;
