import React, { Component } from 'react';
import { Text, View, Button, TextInput } from 'react-native';
class SignupScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user_id: '',
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      isLoading: true,
    };
  }

  static navigationOptions = {
    header: null,
  };

  
  componentDidMount(){
    this.getData(
      )
  }


  validatePassword = () => {
    const { password } = this.state;
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/;
    if (!passwordRegex.test(password)) {
      this.setState({
        passwordError:
          'Password must contain at least 8 characters, one uppercase letter, one lowercase letter, and one number.',
        //add an error msg when it doesn't meet password requirements
        });
      return false;
    } else {
      this.setState({ passwordError: null });
      return true;
    }
  };


  getData(){
    console.log("Get Data")

    return fetch("http://localhost:3333/api/1.0.0/",{
      method: "GET",
      headers: {
      }
    })
  }

  addUserData = () => {
    const isValidPassword = this.validatePassword();

    if (isValidPassword) {
      return fetch('http://localhost:3333/ap/1.0.0', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: this.state.user_id,
          first_name: this.state.first_name,
          last_name: this.state.last_name,
          email: this.state.email,
          password: this.state.password,
        }),
      })
        .then((response) => {
          if (response.status === 201) {
            return response.json();
          } else if (response.status === 400) {
            throw 'Bad Request';
          } else {
            throw 'Server Error';
          }
        })
        .then((rJson) => {
          console.log(rJson);
          this.setState({ error: 'User Added Successfully' });
          this.setState({ Submitted: false });
          this.props.navigation.navigate('Login');
        })
        .catch((error) => {
          this.setState({ error: error });
          this.setState({ Submitted: false });
        });
    }
  };

  render(){

    const navigation = this.props.navigation;
    return(
        <View>
          <Text> To Register enter your details below:</Text>


          <TextInput
              placeholder="Enter Your First Name:"
              onChangeText={(first_name) => this.setState({first_name})}
              value={this.state.first_name}
              style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}

          />


          <TextInput
              placeholder="Enter Your Last Name:"
              onChangeText={(last_name) => this.setState({last_name})}
              value={this.state.last_name}
              style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}

          />

          <TextInput
              placeholder="Enter Your Email:"
              onChangeText={(email) => this.setState({email})}
              value={this.state.email}
              style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}

          />


          <TextInput
              placeholder="Enter Your Password:"
              onChangeText={(password) => this.setState({password})}
              value={this.state.password}
              style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}

          />




          <TextInput
              placeholder="Enter Your Password Again:"
              onChangeText={(password) => this.setState({password})}
              value={this.state.password}
              style={{padding:10, borderWidth:2, borderRadius: 5, margin:10}}

          />

        
        {/* <Text for="Password"><b></b></Text>
        <TextInput type="text" placeholder="Enter Your Password Again:" name="Password" required 
        
        style={{ 
          borderWidth: 1, 
          borderColor: 'black', 
          borderRadius: 5, 
          paddingLeft: 40, 
          height: 40, 
          width: '100%', 
          marginTop: 10 
        }}
        
        
        
        /> */}
        

        <Button
                title="Register"
                onPress={() => this.addUserData() + this.props.navigation.goBack() } />


          <Button 
            title='Back'
            onPress={() => this.props.navigation.goBack()} />

        
        </View>
    );
  }
}

export default SignupScreen;
