import React, { Component } from 'react';
import { Text, View, Button, TextInput, Alert, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class BlockedScreen extends Component {
  state = {
    blockedUsers: [],
  };

  componentDidMount() {
    this.getBlockedList();
  }

  async getBlockedList() {
    try {
      const response = await fetch("http://localhost:3333/api/1.0.0/blocked", {
        method: "GET",
        headers: {
          'Content-Type': 'application/json',
          "X-Authorization": await AsyncStorage.getItem("whatsthat_session_token")
        }
      });
  
      if (response.status === 200) {
        const data = await response.json();
        this.setState({ blockedUsers: data });
      } else if (response.status === 401) {
        throw "Unauthorised";
      } else {
        throw "Server Error";
      }
    } catch (error) {
      Alert.alert("Error", error.toString());
    }
  }  

  async unblockUser(user_id) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/user/${user_id}/block`, {
        method: "DELETE",
        headers: {
          'Content-Type': 'application/json',
          "X-Authorization": await AsyncStorage.getItem("whatsthat_session_token"),
        },
      });

      if (response.status === 200) {
        Alert.alert("Success", "User unblocked successfully");
        this.getBlockedList();
      } else if (response.status === 400) {
        throw "You can't unblock yourself";
      } else if (response.status === 401) {
        throw "Unauthorised";
      } else if (response.status === 404) {
        throw "Not Found";
      } else {
        throw "Server Error";
      }
    } catch (error) {
      Alert.alert("Error", error.toString());
    }
  }
  render() {
    return (
      <View>
        {this.state.blockedUsers.map((user, index) => (
          <View key={index} style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 10 }}>
            <Text style={{ fontSize: 20 }}>{`${user.first_name} ${user.last_name}`}</Text>
            <Button
              title="Unblock User"
              onPress={() => this.unblockUser(user.user_id)}
            />
          </View>
        ))}
      </View>
    );
  }
  
}

export default BlockedScreen;
