import React, { Component } from 'react';
import { Text, View, TextInput, Button, AsyncStorage, Alert } from 'react-native';
//import AsyncStorage from '@react-native-async-storage/async-storage';


class NewChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newChatUserId: '',
      contacts: [],
    };
  }

  componentDidMount() {
    this.fetchContacts();
  }

  // fetchContacts = async () => {
  //   try {
  //     const response = await fetch('http://localhost:3333/api/1.0.0//contacts', {
  //       headers: {
  //         'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
  //       },
  //     });

  //     if (response.ok) {
  //       const contacts = await response.json();
  //       this.setState({ contacts });
  //     }
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  createNewChat = async () => {
    const { newChatUserId, contacts } = this.state;

    if (newChatUserId && contacts.includes(newChatUserId)) {
      try {
        await this.startConversation([newChatUserId]);
        Alert.alert('Success', 'New chat created successfully');
      } catch (error) {
        Alert.alert('Error', error.toString());
      }
    } else {
      Alert.alert('Error', 'User ID is not part of your contacts');
    }
  };

  handleChatIdInputChange = (text) => {
    this.setState({ newChatUserId: text });
  }

  async startConversation(userIds) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chat', {
        method: 'POST',
        headers: {
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ users: userIds }),
      });

      if (response.status === 201) {
        const chatDetails = await response.json();
        this.props.navigation.navigate('ChatDetails');
      } else if (response.status === 400) {
        throw 'Bad Request';
      } else if (response.status === 401) {
        throw 'Unauthorised';
      } else {
        throw 'Server Error';
      }
    } catch (error) {
      Alert.alert('Error', error.toString());
    }
  }

  render() {
    const { newChatUserId } = this.state;

    return (
      <View>
        <TextInput
          placeholder="Enter user ID"
          value={newChatUserId}
          onChangeText={this.handleChatIdInputChange}
        />
        <Button title="Create Chat" onPress={this.createNewChat} />
      </View>
    );
  }
}

export default NewChatScreen;
