import React, { Component } from 'react';
import { Text, View, TextInput, Button, FlatList, AsyncStorage } from 'react-native';

class ChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      chatDetails: null,
      messages: [],
      error: null,
      submitted: false,
      newChatUserId: '',
      newMessage: '',
    };
  }

  componentDidMount() {
    this.viewChats();
  }


  async viewChatDetails(chatId) {
    try {
      const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}', {
        method: 'GET',
        headers: {
          'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
    } else if (response.status === 403) {
        console.log('Forbidden');
    } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Something Went Wrong';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }


 
  



    



async DeleteMessage(chatId, messageId) {
  try {
    const response = await fetch('http://localhost:3333/api/1.0.0/chats/${chatId}/messages/${messageId}', {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        'X-Authorisation': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    });

    if (response.status === 200) {
      const chatDetails = await response.json();
      this.setState({ chatDetails: chatDetails });
    } else if (response.status === 401) {
      console.log('Unathorised');
      await AsyncStorage.removeItem('whatsthat_session_token');
      await AsyncStorage.removeItem('whatsthat_user_id');
      this.props.navigation.navigate('Login');
    } else if (response.status === 403) {
      console.log('Forbidden');
    } else if (response.status === 404) {
      console.log('Not Found');
    } else {
      throw 'Server Error';
    }
  } catch (error) {
    this.setState({ error: error });
  }
}


render() {
  const { chats, newChatUserId, newMessage } = this.state;

  return (
    <View>
      <Text>Create a new chat:</Text>
      <TextInput
        placeholder="Enter user ID"
        onChangeText={(newChatUserId) => this.setState({ newChatUserId })}
        value={newChatUserId}
      />
      <Button title="Create chat" onPress={() => this.startConversation([newChatUserId])} />

      <Text>Current chats:</Text>
      <FlatList
        data={chats}
        renderItem={this.renderChat}
        keyExtractor={(item) => item.chat_id.toString()}
      />

      <Text>Send a message:</Text>
      <TextInput
        placeholder="Enter your message"
        onChangeText={(newMessage) => this.setState({ newMessage })}
        value={newMessage}
      />
      <Button title="Send message" onPress={() => this.SendMessage(this.state.chatDetails.chat_id, newMessage)} />
    </View>
  );
}
}

export default ChatScreen;
