class AddToChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      chatDetails: null,
      messages: [],
      error: null,
      submitted: false,
      newChatUserId: '',
      newMessage: '',
      // Fetch chatId from navigation parameters
      chat_id: this.props.route.params.chatId,
      user_id: '', 
    };
  }

  async componentDidMount() {
    this.AddUserToChat(this.state.chatId, this.state.userId);
  }
  async AddUserToChat(chatId, user_id) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chats/${chatId}/users/${user_id}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });
      
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
        console.log(`User with ID ${user_id} added to chat with ID ${chatId}`);
      }else if (response.status === 400) {
          console.log('Bad Request');
      } else if (response.status === 401) {
          console.log('Unathorised');
          await AsyncStorage.removeItem('whatsthat_session_token');
          await AsyncStorage.removeItem('whatsthat_user_id');
          this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
          console.log('Forbidden');
      } else if (response.status === 404) {
          console.log('Not Found');
      } else {
          throw 'Server Error';
      }
    } catch (error) {
      console.log("Failed to start chat: ", error);
      Alert.alert('Error', error.toString());
    }
  }
  
  render() {
    return (
      <View>
        
        <TextInput
          placeholder="Search For Contacts"
          value={searchQuery}
          onChangeText={this.SpecificContactSearch}
        />

        <Button title="Search Contacts" onPress={this.SearchContacts} />


        <FlatList
          data={filteredContacts}
          keyExtractor={(item) => item.user_id}
          renderItem={({ item }) => (
            <View>
              <Text>{`${item.first_name} ${item.last_name}`}</Text>
              <Button title="Add to Chat" onPress={() => this.AddToChat(item.user_id)} />
            </View>
          )}
        />
      </View>
    );
  }
}


export default AddToChatScreen;
