// AddContactsScreen.js

import React, { Component } from 'react';
import { Text, View, TextInput, Button } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

class AddContactsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      user_id: '',
     };
    }

    async AddUser() {
      try {
        const response = await fetch('http://localhost:3333/api/1.0.0/user/{user_id}/contact', {
          method: 'POST',
          headers: {
            'Authorization': await AsyncStorage.getItem('whatsthat_user_id'),
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            user_id: this.state.user_id,
          }),
        });
    
        if (response.status === 200) {
          const contact = await response.json();
          this.props.navigation.navigate('Contact', { contact });
        } else if (response.status === 400) {
          throw 'You Cant add yourself as a contact';
        } else if (response.status === 401) {
          throw 'Unauthorised';
        } else if (response.status === 404) {
          throw 'Not Found';
        } else {
          throw 'Server Error';
        }
      } catch (error) {
        console.error(error);
      }
    }
    

  render() {
    return (
      <View>
        <Text>This is the Add Contacts screen</Text>
        <TextInput
          placeholder="Enter The User ID Of The Person You Want To Add"
          onChangeText={(user_id) => this.setState({ user_id })}
          value={this.state.user_id}
          style={{
            padding: 10,
            borderWidth: 2,
            borderRadius: 5,
            margin: 10,
          }}
        />

        <Button title="Add Contact" onPress={() => this.AddUser()} />
      </View>
    );
  }
}

export default AddContactsScreen;
