import React, { Component } from 'react';
import { Text, View, TextInput, Button, FlatList, AsyncStorage } from 'react-native';

class UpdateChatScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chats: [],
      chatDetails: null,
      messages: [],
      error: null,
      submitted: false,
      newChatUserId: '',
      newMessage: '',
    };
  }

  
  componentDidMount() {
    this.focusListener = this.props.navigation.addListener('focus', () => {
      this.fetchContacts();
    });
  }

  componentWillUnmount() {
    if (this.focusListener != null && this.focusListener.remove) {
      this.focusListener.remove();
    }
  }

  fetchContacts = async () => {
    try {
      const response = await this.getContacts();
      this.setState({ contacts: response });
      this.filterContacts();
    } catch (error) {
      console.error(error);
    }
  };

  getContacts = async () => {
    const response = await fetch('http://localhost:3333/api/1.0.0/contacts', {
      method: 'GET',
      headers: {
        'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
      },
    });
    if (response.status !== 200) {
      throw new Error('Failed to fetch contacts');
    }
    const contacts = await response.json();
    return contacts;
  };

  filterContacts = () => {
    const { searchQuery, contacts } = this.state;
    const filteredContacts = contacts.filter(
      (contact) =>
        String(contact.user_id).toLowerCase().includes(searchQuery.toLowerCase()) ||
        String(contact.first_name).toLowerCase().includes(searchQuery.toLowerCase()) ||
        String(contact.last_name).toLowerCase().includes(searchQuery.toLowerCase())
    );
    this.setState({ filteredContacts });
  };

  SpecificContactSearch = (searchQuery) => {
    this.setState({ searchQuery }, this.filterContacts);
  };

  AddToChat = (user_id) => {
    const { chat_id } = this.state;

    if (user_id && chat_id) {
      this.AddUserToChat(chat_id, user_id);
    }
  };

  AddUserToChat = async (chat_id, user_id) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}/user/${user_id}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails });
        console.log(`User with ID ${user_id} added to chat with ID ${chat_id}`);
      } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unauthorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw new Error('Server Error');
      }
    } catch (error) {
      console.log('Failed to add user to chat:', error);
      Alert.alert('Error', error.toString());
    }
  };

  RemoveUserFromChat = async (chat_id, user_id) => {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}/user/${user_id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
      });

      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails });
        console.log(`User with ID ${user_id} Deleted From Chat ID ${chat_id}`);
      } else if (response.status === 401) {
        console.log('Unauthorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw new Error('Server Error');
      }
    } catch (error) {
      console.log('Failed to add user to chat:', error);
      Alert.alert('Error', error.toString());
    }
  };



async UpdateChatInformation(chat_id, chatData) {
    try {
      const response = await fetch(`http://localhost:3333/api/1.0.0/chat/${chat_id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'X-Authorization': await AsyncStorage.getItem('whatsthat_session_token'),
        },
        body: JSON.stringify(chatData),
      });
  
      if (response.status === 200) {
        const chatDetails = await response.json();
        this.setState({ chatDetails: chatDetails });
    } else if (response.status === 400) {
        console.log('Bad Request');
      } else if (response.status === 401) {
        console.log('Unathorised');
        await AsyncStorage.removeItem('whatsthat_session_token');
        await AsyncStorage.removeItem('whatsthat_user_id');
        this.props.navigation.navigate('Login');
      } else if (response.status === 403) {
        console.log('Forbidden');
      } else if (response.status === 404) {
        console.log('Not Found');
      } else {
        throw 'Something Went Wrong';
      }
    } catch (error) {
      this.setState({ error: error });
    }
  }

  


  render() {

    return (
      <View>
        <Text>Hello</Text>
      </View>
    );
  }
}


export default UpdateChatScreen;
